var script_version = "1.4";

function beep() {
    var snd = new Audio("data:audio/wav;base64,");
    snd.play();
}

function parse_int(string) {
    output = "";
    for (var key in string) {
        char = string[key];
        if ((char % 1) === 0) {
            if (!isNaN(parseInt(char))) {
                output += char;
            }
        }
    }
    return parseInt(output);
}

function round_up(x, y) {
    return Math.floor(x / y) * y;
}

function clear_fields(){
    document.querySelector("input.premium-exchange-input[data-resource = 'wood']").value = 0;
    document.querySelector("input.premium-exchange-input[data-resource = 'stone']").value = 0;
    document.querySelector("input.premium-exchange-input[data-resource = 'iron']").value = 0;
}

var infobox = document.createElement("div");
infobox.className = "info_box";
var count = 0;
var next_attempt_count = 0;

if (window.location.href.indexOf("screen=market&mode=exchange") === -1) {
    alert("You need to be in the Premium exchange section of the market");
} else {
    beep();
    document.querySelector('#contentContainer').parentNode.insertBefore(infobox, document.querySelector('#contentContainer'));
    document.addEventListener('keyup', function (event) {
        if (event.keyCode == 81) { // q
            document.querySelector("input.premium-exchange-input[data-resource = 'wood']").value = round_up(wood, wood_rate);
        } else if (event.keyCode == 87) { // w
            document.querySelector("input.premium-exchange-input[data-resource = 'stone']").value = round_up(clay, clay_rate);
        } else if (event.keyCode == 69) { // e
            document.querySelector("input.premium-exchange-input[data-resource = 'iron']").value = round_up(iron, iron_rate);
        } else if (event.keyCode == 82) { // r
            
			clear_fields();
        }
    });
    setInterval(function () {
        count++;

        if (count > next_attempt_count) {
            infobox.innerHTML = "(Version:" + script_version + ") Monitoring market, minimize this window and enjoy but don't open new tab here. " + count;
            var fader = jQuery('#fader');
            if (fader.length == 0 || fader.css('display') == 'none') {

                PremiumExchange.loadData();

                wood = document.querySelector('#premium_exchange_stock_wood').innerHTML;
                clay = document.querySelector('#premium_exchange_stock_stone').innerHTML;
                iron = document.querySelector('#premium_exchange_stock_iron').innerHTML;

                wood_rate = parse_int(document.querySelector('#premium_exchange_rate_wood div img').nextSibling);
                clay_rate = parse_int(document.querySelector('#premium_exchange_rate_stone div img').nextSibling);
                iron_rate = parse_int(document.querySelector('#premium_exchange_rate_iron div img').nextSibling);

                if (wood >= wood_rate || clay >= clay_rate || iron >= iron_rate) {
                    document.querySelector("input.premium-exchange-input[data-resource = 'wood']").value = round_up(wood, wood_rate);
                    document.querySelector("input.premium-exchange-input[data-resource = 'stone']").value = round_up(clay, clay_rate);
                    document.querySelector("input.premium-exchange-input[data-resource = 'iron']").value = round_up(iron, iron_rate);
                    jQuery("#premium_exchange_form").submit();
                    beep();
                }
            } else {
                var confirm = jQuery('.confirmation-box').text();
                if (confirm.indexOf('does not have enough')==-1 && confirm.indexOf('do not have enough')==-1){
                    if (confirm.indexOf('Buy')>-1) {
                        jQuery('.confirmation-box .btn-confirm-yes').click();
                    } 
                }else {
                    jQuery('.confirmation-box .btn-confirm-no').click();
                    clear_fields();

                }
                next_attempt_count = count + 5;
            }
        } else {
            infobox.innerHTML = "Waiting a few seconds before our next attempts. We don't want to get detected easily!" + count;
        }
    }, 1000);
}
